import React, {ReactNode} from 'react'
import BreadcrumbItem from "./BreadcrumbItem";
import BreadcrumbSeparator from "./BreadcrumbSeparator";
import BreadCrumCollapser from "./BreadCrumCollapser";

interface BreadcrumbProps {
    itemsBefore: number,
    itemsAfter: number,
    max: number,
    className?: string,
    separator: string,
    children: any,
}

interface BreadcrumbState {
    expanded: boolean,
    children: any[],
}

class Breadcrumb extends React.Component <BreadcrumbProps, BreadcrumbState> {
    public constructor(props: any) {
        super(props);
        this.state = {
            expanded: false,
            children: React.Children.toArray(this.props.children),
        }
    }

    public renderChildren(): void {
        let children: any[] = this.state.children.map((child: any, index: number) =>
            <BreadcrumbItem key={`breadcrumb-item-${index}`}>
                {child}
            </BreadcrumbItem>
        ).reduce((acc: any, child: any, index: number) => {
            acc.push( child, index !== this.state.children.length - 1
                ? <BreadcrumbSeparator key={'breadcrumb-separator-' + index}>
                    {this.props.separator}
                </BreadcrumbSeparator>
                : '');
            return acc;
        }, []);

        if (!this.state.expanded || children.length <= this.props.max) {
            children = [
                ...children.slice(0, this.props.itemsBefore),
                <BreadCrumCollapser
                    title={'Expand'}
                    key={'collapsed-separator'}
                    onClick={this.setState(
                        (state, props) => {
                            return {
                                expanded: !state.expanded,
                                children: state.children,
                            }
                        })
                    }
                />
            ]
        }
    }

    public render(): ReactNode {
        return (
            <ol className={'pl-1 pl-lg-4 pl-md-4 pl-sm-4 ' + this.props.className}>
                {this.renderChildren()}
            </ol>
        );
    }
}

export default Breadcrumb;
