import React, {ReactNode} from 'react';
import SearchBar from './SearchBar';
import SubmitButton from "./SubmitButton";
import '../../css/Nav.css';
import Svg from '../Assets/Svg';
import Request from "../../API/Request";
import ConnectedStatus from "../Connection/ConnectedStatus";

class Nav extends React.Component<any, { tokenExists: boolean }> {
    public constructor(props: any) {
        super(props);
        this.state = {
            tokenExists: false,
        }
        this.disconnect = this.disconnect.bind(this);
        this.checkIfAlreadyConnected = this.checkIfAlreadyConnected.bind(this);

    }

    // Check if token is still valid
    public componentDidMount(): void {
        if (undefined !== localStorage.getItem('token') && '' !== localStorage.getItem('token')) {
            new Request(
                '/connection/verification',
                this.checkIfAlreadyConnected,
            );
        }
    }

    public checkIfAlreadyConnected(data: any): void {
        this.setState(
            {tokenExists: data['token_exists']},
            () => {
                if (false === this.state.tokenExists) {
                    localStorage.setItem('token', '');
                }
            }
        );
    }

    public disconnect(): void {
        localStorage.setItem('token', '');
        // @ts-ignore
        document.location = '/';
    }

    public render(): ReactNode {
        return (
            <nav className={'row mt-3'}>
                <div className={'col-lg-2 col-md-2 col-sm-4 col-xs-12 mr-lg-0 mr-sm-0 text-left'}>
                    <a href={'/'} className={'row container-fluid col-xs-11'}>
                        <Svg
                            className={'col-lg-3 col-md-3 col-sm-3 col-xs-2 pl-0'}
                        />
                        <div
                            className={'col-lg-9 col-md-9 col-sm-9 col-xs-9 mt-3 pl-0'}
                        >
                            <div
                                className={'mt-1'}
                            >
                                <p>
                                    caCompilePas
                                </p>
                            </div>
                        </div>
                    </a>
                    <ConnectedStatus
                        connectedDivClassName={'d-lg-none d-md-none d-sm-none col-xs-1 mb-3'}
                        connectedButtonClassName={'row container-fluid mt-0 pt-0 pl-5 disconnect-button'}
                        notConnectedClassName={'row mt-3 pl-5'}
                        disconnect={this.disconnect}
                        status={this.state.tokenExists}
                    />
                </div>
                <SearchBar/>
                <ConnectedStatus
                    connectedDivClassName={'d-none d-lg-block d-md-block d-sm-block col-lg-1 col-md-1 col-sm-1'}
                    connectedButtonClassName={'col-lg-1 col-md-1 col-sm-1 col-xs-1 container-fluid mt-lg-0 mt-md-0 mt-sm-0 mt-xs-1 pt-lg-0 pt-md-0 pt-sm-0 disconnect-button pl-0 ml-4'}
                    notConnectedClassName={'d-none d-lg-block d-md-block d-sm-block col-lg-1 col-sm-1 mt-lg-3 mt-md-3 mt-sm-1 mt-xs-3 pt-lg-2 pr-lg-5 pr-sm-1 pl-0 mr-5'}
                    disconnect={this.disconnect}
                    status={this.state.tokenExists}
                />
            </nav>
        )
    }
}

export default Nav;
