import React, {ReactNode} from "react";
import '../../css/Message.css';
import Input from "../General/Inputs/Input";
import SubmitButton from "../General/Inputs/SubmitButton";

interface WriteMessageZoneProps {
    labelLobby: string,
    onEnter: (event: React.KeyboardEvent<HTMLDivElement>) => void,
    send?: ((event: React.MouseEvent<HTMLButtonElement>) => void | undefined) |Â undefined,
}

class WriteMessageZone extends React.Component<WriteMessageZoneProps, any> {
    public render(): ReactNode {
        return (
            <div className={'col-lg-12 mt-3 ml-lg-0 ml-lg-2 ml-sm-3 pl-lg-4 text-left'}
                onKeyDown={this.props.onEnter}
            >
                <div className={'form-group '}>

                <textarea
                    id={'message-input'}
                    placeholder={'Ecrire un message sur #' + this.props.labelLobby}
                    className={'form-control mt-0 rounded text-left'}
                    rows={1}
                >
                </textarea>
                    <SubmitButton
                        text={'Envoyer'}
                        onClick={this.props.send}
                        />
                </div>
                <Input
                    id={'message-input'}
                    inputType={'text-area'}
                    placeholder={'Ecrire un message sur #' + this.props.labelLobby}
                    checked={false}
                    className={'text-left'}
                    onChange={(event: any) => null}
                />
            </div>
        )
    }
}

export default WriteMessageZone;
