import React, {ReactNode} from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route, Link,
} from 'react-router-dom';
import Nav from '../Navigation/Nav';
import LobbyPage from '../Lobby/LobbyPage';
import Connection from '../Connection/Connection';
import NotFound from '../Pages/NotFound';
import PrivateRoute from '../../Security/PrivateRoute';
import '../../css/App.css';
import Home from '../Pages/Home';
import Admin from '../Admin/Admin';
import PublicLobbies from '../Public/PublicLobbies';
import CourseSheetPage from '../CourseSheet/CourseSheetPage';
import Personal from '../Pages/Personal';
import LobbyCreation from '../Lobby/LobbyCreation';
import Inscription from '../Connection/Inscription';
import Breadcrumb from "../Navigation/Breadcrumb";
import {MdHome, MdSettings, MdEmail, MdInfo, MdWeb} from 'react-icons/md';

interface AppState {
    items: any[],
    icons: any,
}

class App extends React.Component<any, AppState> {
    public constructor(props: any) {
        super(props);
        this.state = {
            icons: {
                Accueil: MdHome,
                Admin: MdSettings,
                Inscription: MdEmail,
                'Page perso': MdInfo,
            },
            items: [
                {to: '/', label: 'Accueil', pos: 'mt-1', col: 'col-2'},
                {to: '/admin/1', label: 'Admin', pos: 'mt-1', col: 'col-2'},
                {to: '/connexion/register', label: 'Inscription', pos: 'mt-1', col: 'col-3'},
                {to: '/public', label: 'Lobbies publics', pos: 'mt-0', col: 'col-3'},
                {to: '/perso', label: 'Page perso', pos: 'mt-1', col: 'col-3'},
                {to: '/perso', label: 'Page perso', pos: 'mt-1', col: 'col-3'},
                {to: '/perso', label: 'Page perso', pos: 'mt-1', col: 'col-3'},
                {to: '/perso', label: 'Page perso', pos: 'mt-1', col: 'col-3'},
            ]
        };
    }


    public render(): ReactNode {
        return (
            <div className="App container-fluid ml-lg-4">
                <Nav/>
                <Router>
                    <Breadcrumb
                        itemsBefore={4}
                        itemsAfter={1}
                        max={4}
                        separator={'/'}
                    >
                        {this.state.items.map(({to, label, pos, col}) => {
                            const Icon = this.state.icons[label];
                            return (
                                <div key={to} className={'row p-0'}>
                                    <div className={'p-0 ' + col}>
                                        {Icon && <Icon className={'h3 ' + pos}/>}
                                    </div>
                                    <div className={'col-10 p-0 text-center'}>
                                        <Link key={to} to={to}>
                                            {label}
                                        </Link>
                                    </div>
                                </div>
                            )
                        })}
                    </Breadcrumb>
                    <Switch>
                        <Route exact path={'/'}>
                            <Home/>
                        </Route>
                        <PrivateRoute path={'/lobby'} component={LobbyPage} rest={[]}/>
                        <PrivateRoute path={'/course-sheet'} component={CourseSheetPage} rest={[]}/>
                        <Route path={'/connexion/login'}>
                            <Connection referrer={document.referrer}/>
                        </Route>
                        <Route path={'/connexion/register'}>
                            <Inscription/>
                        </Route>
                        <PrivateRoute path={'/admin'} component={Admin} rest={[]}/>
                        <PrivateRoute path={'/creation'} component={LobbyCreation} rest={[]}/>
                        <Route path={'/public'}>
                            <PublicLobbies/>
                        </Route>
                        <PrivateRoute path={'/perso'} component={Personal} rest={[]}/>
                        <Route path={'*'}>
                            <NotFound/>
                        </Route>
                    </Switch>
                </Router>
            </div>
        );
    }
}

export default App;
