import React, {ReactNode} from 'react';
import Request from "../../API/Request";

interface LobbyProposalProps {
    id: string,
    label: string,
    description: string,
    logo: string,
}

class LobbyProposal extends React.Component<LobbyProposalProps, any> {
    public constructor(props: LobbyProposalProps) {
        super(props);

        this.fillLogo = this.fillLogo.bind(this);
        this.getLogo = this.getLogo.bind(this);
    }

    public componentDidMount(): void {
        this.getLogo();
    }

    public getLogo(): void {
        new Request(
            '/lobby/getLogo/0',
            this.fillLogo,
            'POST',
            {
                id_lobby: this.state.id,
                path: this.state.logo,
            },
            'json',
            'blob',
        );
    }

    public fillLogo(data: Blob): void {
        const img: any = document.getElementById('search-lobby-logo' + this.state.id);
        const blob = new Blob([data], {type: 'image/jpg'});
        img.src = URL.createObjectURL(blob);
    }

    public render(): ReactNode {
        return (
            <li key={this.props.id}>
                <a
                    className={'row'}
                    href={'/lobby/' + this.props.id}
                >
                    <div className={'col-2 mt-2'}>
                        <img
                            id={'search-lobby-logo-' + this.props.id}
                            src={this.props.logo}
                            className={'col-1 proposal-icon lobby-logo rounded-circle'}
                            alt={this.props.label}
                        />
                    </div>
                    <div
                        className={'col-11 pl-sm-1 pt-sm-2 proposal'}
                    >
                        {this.props.label} {this.props.description}
                    </div>
                </a>
            </li>
        );
    }
}

export default LobbyProposal;
