import React, {ReactNode} from "react";
import SubmitButton from "../General/SubmitButton";

interface ConnectedStatusProps {
    connectedDivClassName: string
    connectedButtonClassName: string,
    notConnectedClassName: string,
    display: string
    status: boolean,
    disconnect: any,
}

class ConnectedStatus extends React.Component<ConnectedStatusProps, any> {
    public render(): ReactNode {
        if (this.props.status) {
            return (<div className={'container-fluid ' + this.props.connectedDivClassName + ' ' + this.props.display}>
                <div className={'row'}>
                    <SubmitButton
                        text={''}
                        onClick={this.props.disconnect}
                        className={this.props.connectedButtonClassName + ' pb-0 ' + this.props.display}
                        disconnectButton={true}
                    />
                </div>
                <div className={'row'}>
                    Connect√©
                </div>
            </div>);
        } else {
            return (<div className={'pt-lg-0 pt-md-0 pt-sm-3'}>
                <a id={'user'} href={'/connexion/login'}
                   className={this.props.notConnectedClassName + ' ' + this.props.display}
                >
                            <span
                                className={'glyphicon glyphicon-user mt-sm-1'}
                            >
                            </span>
                </a>
            </div>);
        }
    }
}

export default ConnectedStatus;
