import React, {ReactNode} from "react";
import LobbyDescription from "./LobbyDescription";
import LobbySummary from "./LobbySummary";
import LobbyDivider from "./LobbyDivider";
import adminIcon from "../../img/admin.png";
import Request from "../../API/Request";

interface LobbyTopProps {
    id: string,
    lobbyInformation: any,
    courseSheets: [],
}

interface LobbyTopState {
    isAdmin: boolean,
}

class LobbyTop extends React.Component<LobbyTopProps, LobbyTopState> {

    public constructor(props: any) {
        super(props);
        this.state = {
            isAdmin: false,
        }

        this.checkIfAdmin = this.checkIfAdmin.bind(this);
        this.refreshAdmin = this.refreshAdmin.bind(this);
    }

    public componentDidMount(): void {
        this.refreshAdmin();
    }

    public checkIfAdmin(payload: any): void {
        this.setState({isAdmin: payload['success']});
    }

    public refreshAdmin(): void {
        new Request(
            '/user/check_if_admin',
            this.checkIfAdmin,
            'POST',
            {
                'lobby_id': this.props.id,
            }
        );
    }

    public render(): ReactNode {
        return (
            <div className={'row container-fluid pr-0 mt-5 mt-lg-5 mt-md-5 mt-sm-5'}>
                {
                    (() => {
                        return this.state.isAdmin
                            ? <div className={'row col-12 pr-0'}>
                                <div className={'col-12 text-right'}>
                                    <a
                                        href={'/admin/' + this.props.id}
                                    >
                                        Admin du lobby
                                        <img
                                            src={adminIcon}
                                            alt={'Icon de l\'admin'}
                                            className={'admin-icon'}
                                        />
                                    </a>
                                </div>
                            </div>
                            : <div></div>
                    })()
                }
                <LobbyDescription
                    id={this.props.id}
                />
                <div className={'col-12 pl-0 pl-lg-4 pl-md-4 pl-sm-4'}>
                    <div className={'row container-fluid pr-0'}>
                        <LobbyDivider/>
                    </div>
                </div>
                <LobbySummary courseSheets={this.props.courseSheets}/>
            </div>
        )
    }
}

export default LobbyTop;
