import React, {ReactNode} from "react";
import SubmitButton from "../General/SubmitButton";

interface ConnectedStatusProps {
    connectedClassName: string,
    notConnectedClassName: string,
    status: boolean,
    disconnect: any,
}

class ConnectedStatus extends React.Component<ConnectedStatusProps, any> {
    public render(): ReactNode {
        return (
            <div>
                {
                    this.props.status
                        ? (<div className={'container-fluid col-1'}>
                            <div className={'row'}>
                                <SubmitButton
                                    text={''}
                                    onClick={this.props.disconnect}
                                    className={'col-lg-1 col-md-1 col-sm-1 col-xs-1 container-fluid mt-lg-2 mt-md-2 mt-sm-1 mt-xs-1 disconnect-button pl-0 ml-3'}
                                    disconnectButton={true}
                                />
                            </div>
                            <div className={'row'}>
                                Connect√©
                            </div>
                        </div>)
                        : (<div className={'d-none d-lg-block d-md-block d-sm-block pt-lg-0 pt-md-0 pt-sm-3'}>
                            <a id={'user'} href={'/connexion/login'}
                               className={'col-lg-1 col-sm-1 mt-lg-3 mt-md-3 mt-sm-3 mt-xs-3 pt-lg-2 pr-lg-5 pr-sm-1 pl-0 mr-5'}
                            >
                            <span
                                className={'glyphicon glyphicon-user mt-sm-1'}
                            >
                            </span>
                            </a>
                        </div>)
                }
            </div>
        );
    }
}

export default ConnectedStatus;
