import React from 'react';

const Request = (props) => {
  const domain = 'localhost:80';
  let route = '';
  if (undefined !== props.route.split(/\//)[0]) {
    route += '?module=' + props.route.split(/\//)[0] ? props.route.split(/\//)[0] : '';
    if (undefined !== props.route.split(/\//)[1]) {
      route += '&action=' + props.route.split(/\//)[1] ? props.route.split(/\//)[1] : '';
      if (undefined !== props.route.split(/\//)[2]) {
        route += '&param=' + props.route.split(/\//)[2] ? props.route.split(/\//)[2] : '';
      }
    }
  }
  const method = props.method;
  const data = props.data;
  const JSONResponse = null;
  fetch(domain + route, {
    method: method,
    headers: 'POST' === method ? {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    } : null,
    body: 'POST' === method ? JSON.stringify(data) : null
  })
  .then(response => response.json())
  .then(jsonResponse => JSONResponse = jsonResponse)
  .catch(networkError => console.log(networkError));
  return <div/>;
}

export default Request;
