import React from 'react';

class Route extends React.Component {
  constructor(props) {
    super(props);
    this.getRoute = this.getRoute.bind(this);
    this.state = {
      route: this.getRoute()
    }
    this.displayIfURLPathMatches = this.displayIfURLPathMatches.bind(this);
  }
  displayIfURLPathMatches() {
    if (this.props.path === this.state.route)
      return this.props.children;
  }
  getRoute() {
    let route = '/';
    for (let i = 3; i < document.URL.split(/\//).length; i++) {
      route += document.URL.split(/\//)[i];
    }
    return route;
  }
  render() {
    return (
      <div>
        { this.displayIfURLPathMatches() }
      </div>
    )
  }
}

export default Route;
