import React, {ChangeEvent, ReactNode} from 'react';
import styled from 'styled-components';
import {ReactComponent as DropBoxLogo} from "../../img/usable-image-icon.svg";
import Request from "../../API/Request";


const Heading = styled.p<{ active: boolean }>`
  color: ${(props: { active: any }) => props.active ? "red" : "blue"};
  text-align: center;
`;

class DropBoxBackground extends React.Component<any, any> {
    public render(): ReactNode {
        return (
            <div
                style={{
                    backgroundColor: '#ffffff',
                    width: '200px',
                    height: '145px',
                }}
                className={'rounded pt-4 mt-5'}
            >
                {this.props.children}
                <Heading active={false}>
                    Glisse un logo par ici !
                </Heading>
            </div>
        );
    }
}

interface DropBoxProps {
    id: string,
    className: string,
    handleFileDrop: (event: React.DragEvent<HTMLDivElement>) => void,
    handleFileChange: (event: ChangeEvent<HTMLInputElement>) => void,
}

class DropBox extends React.Component<DropBoxProps, { hidden: boolean }> {
