import React, { ReactDOM, useState, useEffect } from 'react';
import Nav from './Nav';
import Footer from './Footer';
import Lobby from './Lobby';
import './App.css';

const Canvas = () => {
  const canvasRef = React.useRef(null)
  let [canvas, setCanvas] = useState(null);
  useEffect(() => {
    canvas = canvasRef.current
    console.log('canvas1', canvas)
    setCanvas(canvas)
  }, []);
  const getCanvas = () => {
    return canvas;
  }
  return (
    <div>
      <canvas 
        ref={ canvasRef }
        width={ window.innerWidth }
        height={ window.innerHeight }
        onClick={e => {
          let x = e.clientX
          let y = e.clientY
          let coor = [ x, y ]
          console.log(coor)
        }}>
          <Figure canvas={ getCanvas }/>
        </canvas>
    </div>
  )
}

const Figure = (props) => {
  console.log(props)
  useEffect(() => {
    console.log('props', props)
    const canvas = props.canvas
    console.log('canvas', canvas)
    const ctx = canvas.getContext('2d')
    console.log(ctx)
  })

  return <div></div>
}

const Bakground = (props) => {
  const renderFigure = () =>  <Canvas/>
  return (
    <div>
      { renderFigure() }
      { props.children }
    </div>
  )
}

function App() {
  return (
    <div className="App container-fluid ml-lg-4">
      <Bakground>
      <Nav/>
      <Lobby/>
      <Footer/></Bakground>
    </div>
  );
}

export default App;
