import React, { ReactDOM, useState, useEffect } from 'react';
import Nav from './Nav';
import Footer from './Footer';
import Lobby from './Lobby';
import './App.css';

const Canvas = () => {
  const canvasRef = React.useRef(null)
  let [canvas, setCanvas] = useState(null);
  const drawCircle = (y, x, size) => {
    const ctx = canvas.getContext('2d')
    ctx.arc(y, x, size, 100, 2 * Math.PI)
    ctx.stroke()
  }
  useEffect(() => {
    canvas = canvasRef.current
    setCanvas(canvas)
    drawCircle(100, 75, 50)
  });
  return (
    <div>
      <canvas 
        ref={ canvasRef }
        width={ window.innerWidth }
        height={ window.innerHeight }
        onClick={e => {
          let x = e.clientX
          let y = e.clientY
          let coor = [ x, y ]
          console.log(coor)
        }}>
        </canvas>
    </div>
  )
}

const Bakground = (props) => {
  const renderFigure = () =>  <Canvas/>
  return (
    <div>
      { renderFigure() }
      { props.children }
    </div>
  )
}

function App() {
  return (
    <div className="App container-fluid ml-lg-4">
      <Bakground>
      <Nav/>
      <Lobby/>
      <Footer/></Bakground>
    </div>
  );
}

export default App;
