import React, {ReactNode} from "react";
import Request from "../../API/Request";
import {pdfjs} from "react-pdf";

interface CourseSheetPageProps {
    id: number,
    location: any,
}

class CourseSheetPage extends React.Component<CourseSheetPageProps, any> {

    public constructor(props: CourseSheetPageProps) {
        super(props);
        this.getFile = this.getFile.bind(this);
        this.openFile = this.openFile.bind(this);
        this.onDocumentLoadSuccess = this.onDocumentLoadSuccess.bind(this);
    }

    public componentDidMount(): void {
        this.refreshPreview();
        this.fetchHashtags();
    }

    public getFile(): void {
        new Request(
            '/lobby/coursesheet/' + this.props.id,
            this.fillPreview,
            'POST',
            {
                path: this.props.link
            },
            'json',
            'blob',
        );
    }

    public openFile(data: any): void {
        this.setState(
            {file: data},
            () => pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`
        );
    }

    public onDocumentLoadSuccess(): void {

    }

    public render(): ReactNode {
        return (
            <div>
                Fiche de cours {this.props.location.pathname.split(/\//)[2]}
            </div>
        );
    }
}

export default CourseSheetPage;
