import React, { ReactDOM, useState, useEffect } from 'react';
import Nav from './Nav';
import Footer from './Footer';
import Lobby from './Lobby';
import './App.css';

const Canvas = () => {
  const canvasRef = React.useRef(null)
  let [canvas, setCanvas] = useState(null)
  const [objects] = useState([])
  const ctx = canvas.getContext('2d')
  const drawCircle = (y, x, radius) => {
    let object = {
      height: 40,
      height: 40,
      x: 10,
      y: 10,
      radius: 50,
      color: '#FF0000'
    }
    objects.push(object)
    ctx.beginPath()
    ctx.arc(y, x, radius, 0, 2 * Math.PI)
    ctx.stroke()
    ctx.fill()
    ctx.strokeStyle = 'white'
    ctx.fillStyle = 'white'
    ctx.x = 600
    ctx.closePath()
  }
  const renderObject = () => {
    for (obj of objects) {
      drawCircle(obj.y, obj.y, obj.radius)
    }
  }
  const move = () => {

  }
  useEffect(() => {
    canvas = canvasRef.current
    setCanvas(canvas)
    drawCircle(100, 75, 50)
    drawCircle(200, 300, 50)
  });
  return (
    <div>
      <canvas 
        ref={ canvasRef }
        width={ window.innerWidth }
        height={ window.innerHeight }
        onClick={e => {
          let x = e.clientX
          let y = e.clientY
          let coor = [ x, y ]
          console.log(coor)
        }}>
        </canvas>
    </div>
  )
}

const Bakground = (props) => {
  const renderFigure = () =>  <Canvas/>
  return (
    <div>
      { renderFigure() }
      { props.children }
    </div>
  )
}

function App() {
  return (
    <div className="App container-fluid ml-lg-4">
      <Bakground>
      <Nav/>
      <Lobby/>
      <Footer/></Bakground>
    </div>
  );
}

export default App;
