import React, {ChangeEvent, ReactNode} from 'react';
import styled from 'styled-components';
import { ReactComponent as DropBoxLogo } from "../../img/usable-image-icon.svg";
import Request from "../../API/Request";


const Heading = styled.p<{ active: boolean }>`
  color: ${(props: { active: any }) => props.active ? "red" : "blue"};
  text-align: center;
`;

class DropBoxBackground extends React.Component<any, any> {
    public render(): ReactNode {
        return (
            <div
                style={{
                    backgroundColor: '#ffffff',
                    width: '30vh',
                    height: '20vh',
                    zIndex: 0,
                }}
                className={'rounded-1 pt-5 mt-5'}
            >
                {this.props.children}
                <Heading active={false}>
                    Glisse une image ici !
                </Heading>
            </div>
        );
    }
}

class DropBox extends React.Component<{ id: string, className: string }, { hidden: boolean }> {
