    public handleDragEnter(event: React.DragEvent<HTMLDivElement>): void {
        console.log('drag enter');
    }

    public handleDragLeave(event: React.DragEvent<HTMLDivElement>): void {
        console.log('drag leave');
    }

    public handleDragOver(event: React.DragEvent<HTMLDivElement>): void {
        event.preventDefault();
    }

    public handleDrop(event: React.DragEvent<HTMLDivElement>): void {
        event.preventDefault();
        this.file = event.dataTransfer.files[0];
    }

    public render(): ReactNode {
        return (
            <DropBoxBackground>
                <div
                    onDragEnter={this.handleDragEnter}
                    onDragLeave={this.handleDragLeave}
                    onDragOver={this.handleDragOver}
                    onDrop={this.handleDrop}
                >
                    <label>
                        <input
                            ref={'input'}
                            type={'file'}
                            id={this.props.id}
                            className={this.props.className}
                            onChange={this.handleFileChange}
                            hidden={this.state.hidden}
                            accept={'image/*'}/>
                            <div
                                className={'rounded-1'}
                                style={{
                                    padding: '10px',
                                    paddingTop: '7px',
                                    margin: '10px',
                                    borderRadius: '5px',
                                    backgroundColor: 'gray',
                                }}
                            >
                                <DropBoxLogo/>
                            </div>
                    </label>
                </div>
            </DropBoxBackground>
        );
    }
}

export default DropBox;
