import React, { ReactDOM, useState, useEffect } from 'react';
import Nav from './Nav';
import Footer from './Footer';
import Lobby from './Lobby';
import './App.css';

const Canvas = () => {
  const canvasRef = React.useRef(null)
  useEffect(() => {
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    ctx.beginPath();
    ctx.arc(100, 75, 50, 0, 2 * Math.PI)
    ctx.stroke();
  }, []);

  return (
    <div>
      <canvas 
        ref={ canvasRef }
        width={ window.innerWidth }
        height={ window.innerHeight }
        onClick={e => {
          let x = e.clientX
          let y = e.clientY
          let coor = [ x, y ]
        }}/>
    </div>
  )
}

const Bakground = (props) => {
  const renderFigure = () =>  <Canvas/>
  return (
    <div>
      { renderFigure() }
      { props.children }
    </div>
  )
}

function App() {
  return (
    <div className="App container-fluid ml-lg-4">
      <Bakground>
      <Nav/>
      <Lobby/>
      <Footer/></Bakground>
    </div>
  );
}

export default App;
