import React from 'react';
import Route from './Route';
import Lobby from './Lobby';

class Switch extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
		}
		this.chooseRoute = this.chooseRoute.bind(this);
	}
	componentDidMount() {
		console.log('alalal');
		console.log(document.URL.split(/\//));
		// this.state.children[0].props.children
	}
	render() {
		return (
			<div>
              { this.chooseRoute() }
			</div>
		)
	}
}

class Router extends React.Component {
    constructor(props) {
    	super(props);
        console.log(this.props.children);
        this.chooseRoute = this.chooseRoute.bind(this);
    }
    chooseRoute() {
        switch(document.URL.split(/\//)[3]) {
            case 'connexion':
                return <p>Connexion</p>;
                break;
            case 'lobby':
                return <Lobby/>;
                break;
            default:
                return <p>Home</p>;
                break;
         }
    }
    render() {
        return(
        <div>
            <Route path='/lobby'>
                <Lobby/>
            </Route>
            <Route path='/connexion'>
                <p>Connexion</p>
            </Route>
        </div>
	   )
    }
}

export default Router;
