import React, {ReactNode} from "react";
import Request from "../../API/Request";
import '../../css/PopularLobbies.css';

interface PopularLobbyProps {
    id: number,
    label: string,
    description: string,
    logo: string,
    pseudo: string
}
class PopularLobby extends React.Component<PopularLobbyProps, any> {
    public constructor(props: PopularLobbyProps) {
        super(props);
        this.fillLogo = this.fillLogo.bind(this);
        this.getLogo = this.getLogo.bind(this);
    }

    public componentDidMount(): void {
        this.getLogo();
    }

    public getLogo(): void {
        new Request(
            '/lobby/getLogo/0',
            this.fillLogo,
            'POST',
            {
                idLobby: this.props.id,
                path: this.props.logo,
            },
            'json',
            'blob',
        );
    }

    public fillLogo(data: Blob): void {
        const img: any = document.getElementById('lobby-logo' + this.props.id);
        const blob = new Blob([data], {type: 'image/jpg'});
        img.src = URL.createObjectURL(blob);
    }

    public render(): ReactNode {
        return (
            <div className={'container-fluid row mt-sm-4 pr-0 mb-lg-5 mr-4 pl-0 '
                + (0 === this.props.id % 2 ? 'col-lg-5 ml-lg-2' : 'col-lg-6')
            }>
                <div className={'col-lg-3 col-md-3 col-sm-2 lobby-logo pt-lg-5 pt-md-5 pt-sm-5'}>
                    <img
                        id={'lobby-logo' + this.props.id}
                        className={'lobby-logo'}
                        src={this.props.logo}
                        alt={'Lobby ' + this.props.label + ' logo'}
                        />
                </div>
                <div className={'col-lg-9 col-md-9 col-sm-10 mb-0 p-lg-0'}>
                    <div className={'row'}>
                        <h3 className={'m-0'}>{this.props.label}</h3>
                    </div>
                    <div className={'row lobby-description mt-0'}
                         style={{
                             verticalAlign: 'top',
                             margin: 'auto',
                         }}
                    >
                        <p
                            style={{
                                verticalAlign: 'top',
                            }}>{this.props.description}</p>
                    </div>
                    <div className={'row mt-lg-5 mt-md-5 mt-sm-0'}>
                        <div className={'col-lg-8 col-md-8 col-sm-8 col-xs-8 text-left p-0'}>
                            <a
                                href={'/lobby/' + this.props.id}
                            ><p>Lien vers le lobby</p></a>
                        </div>
                        <div className={'col-lg-4 col-md-4 col-sm-4 col-xs-4 p-0'}>
                            <p className={' text-right'}>{this.props.pseudo}</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default PopularLobby;
