import React, {ChangeEvent, ReactNode} from 'react';
import styled from 'styled-components';
import {ReactComponent as DropBoxLogo} from "../../img/usable-image-icon.svg";

const Heading = styled.p<{ active: boolean }>`
  color: #5757e7;
  text-align: center;
`;

class DropBoxBackground extends React.Component<{label: string}, any> {
    public render(): ReactNode {
        return (
            <div
                style={{
                    backgroundColor: '#ffffff',
                    width: '220px',
                    height: '145px',
                }}
                className={'rounded pt-4 mt-4'}
            >
                {this.props.children}
                <Heading active={false}>
                    {this.props.label}
                </Heading>
            </div>
        );
    }
}

interface DropBoxProps {
    id: string,
    className: string,
    label: string,
    handleFileDrop: (event: React.DragEvent<HTMLDivElement>) => void,
    handleFileChange: (event: ChangeEvent<HTMLInputElement>) => void,
}

class DropBox extends React.Component<DropBoxProps, { }> {
    private file: File | null;

    constructor(props: any) {
        super(props);
        this.file = null;
        this.handleDragOver = this.handleDragOver.bind(this);
        this.handleDrop = this.handleDrop.bind(this);
    }

    public handleDragOver(event: React.DragEvent<HTMLDivElement>): void {
        event.preventDefault();
    }

    public handleDrop(event: React.DragEvent<HTMLDivElement>): void {
        event.preventDefault();
        this.file = event.dataTransfer.files[0];
    }

    public render(): ReactNode {
        return (
            <div
                className={this.props.className}
                onDragOver={this.handleDragOver}
                onDrop={this.props.handleFileDrop}
            >
                <label>
                    <DropBoxBackground label={this.props.label}>
                        <input
                            type={'file'}
                            id={this.props.id}
                            onChange={this.props.handleFileChange}
                            hidden
                            accept={'image/*'}/>
                        <div
                            className={'rounded-1'}
                            style={{
                                padding: '10px',
                                margin: '10px',
                                borderRadius: '5px',
                                backgroundColor: '#e6e6e6',
                                width: '40%',
                                height: '50%',
                                marginLeft: 'auto',
                                marginRight: 'auto',
                            }}
                        >
                            <DropBoxLogo/>
                        </div>
                    </DropBoxBackground>
                </label>
            </div>
        );
    }
}

export default DropBox;
