import React, {ReactNode, Ref} from 'react';

class Hashtag extends React.Component<{ text: string }, { width: number}> {
    private hashtagBox: Ref<HTMLDivElement>;

    public constructor(props: any) {
        super(props);
        this.state = {
            width: 0,
        }
        this.hashtagBox = React.createRef();
    }

    public componentDidMount(): void {
        // @ts-ignore
        let box: any |Â null = this.hashtagBox.current;
        this.setState(
            {
                width:
                    box.width
            });
        console.log(this.state.width);
    }

    public render(): ReactNode {
        let div = (
            <div key={this.props.text}
                 style={{
                     backgroundColor: 'black',
                     opacity: 0.3,
                     zIndex: 1,
                 }}
                 className={'rounded p-1 pl-2 pr-2 m-1'}
                 ref={this.hashtagBox}
            >
                        <span
                            style={{
                                color: 'white',
                                fontWeight: 'bold',
                            }}
                        >
                            #{this.props.text}
                        </span>
            </div>
        );
        return div;
    }
}

export default Hashtag;
