import React from 'react';

const Request = (props) => {
  const domain = 'localhost:80';
  let route = '/';
  if (undefined !== props.route.split(/\//)[1]) {
    route += '?module=' + props.route.split(/\//)[1];
    if (undefined !== props.route.split(/\//)[2]) {
      route += '&action=' + props.route.split(/\//)[2];
      if (undefined !== props.route.split(/\//)[3]) {
        route += '&param=' + props.route.split(/\//)[3];
      }
    }
  }
  const method = props.method;
  const data = props.data;
  console.log(data);
  let JSONResponse = null;
  console.log('route', domain + route);
  fetch(domain + route, {
    method: method,
    headers: 'POST' === method ? {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    } : null,
    body: 'POST' === method ? JSON.stringify(data) : null
  })
  .then(response => response.json())
  .then(jsonResponse => {
    JSONResponse = jsonResponse;
    console.log(JSONResponse);
  })
  .catch(networkError => console.log(networkError));
  fetch('http://localhost:80/?module=connection&action=login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({"pseudo":"toto","password":"pass"})
  })
  .then(() => console.log('ok'))
  .catch(error => console.log(error));
  return <div/>;
}

export default Request;
