import React, {ChangeEvent, ReactNode} from 'react';
import Hashtag from "./Hashtag";

interface SearchBarState {
    text: string,
    hashtags: string[],
    hashtagsWidth: number,
}

class SearchBar extends React.Component<any, SearchBarState> {

    constructor(props: any) {
        super(props);
        this.state = {
            text: '',
            hashtags: [],
            hashtagsWidth: 0,
        }
        this.write = this.write.bind(this);
        this.renderHashtags = this.renderHashtags.bind(this);
        this.addHashtag =  this.addHashtag.bind(this);
    }

    public write(event: ChangeEvent<HTMLInputElement>): void {
        this.setState({text: event.target.value});
    }

    public async addHashtag(event: React.KeyboardEvent<HTMLInputElement>): Promise<void> {
        if (13 === event.keyCode) {
            event.preventDefault();
            const newHashtag = (event.target as HTMLInputElement).value;
            if (!this.state.hashtags.includes(newHashtag)) {
                this.state.hashtags.push(newHashtag);
                this.forceUpdate(() => (document.getElementById('search') as HTMLInputElement).value = '');
            }
        }
    }

    public renderHashtags(): ReactNode {
        const res = this.state.hashtags.map(
            (hashtag: string) =>
                    <Hashtag key={hashtag} text={hashtag}/>
        );
                console.log('ho');
                console.log(hashtag);
                /*this.setState((state, props) => {
                    // @ts-ignore
                    return {hashtagsWidth: state.hashtagsWidth + hashtag.getWidth()};
                })*/
        return res;
    }

    public render(): ReactNode {
        return (
            <form
                className="form-inline my-lg-0 my-md-0 my-sm-2 my-xs-2 col-lg-5 col-md-5 col-sm-6 col-xs-6 offset-lg-1 offset-md-1 offset-sm-0 offset-xs-0">
                <label id="search-icon" htmlFor="search">
                    <span className="glyphicon glyphicon-search search-icon"></span>
                    <span id="search-placeholder">{this.state.text === '' && 0 === this.state.hashtags.length ? 'Search...' : ''}</span>
                </label>
                {this.renderHashtags()}
                <input id="search" className="form-control col-lg-12 w-75 mr-sm-2" type="search"
                       aria-label="Search"
                       onChange={event => this.write(event)}
                       value={this.state.text}
                       onKeyDown={this.addHashtag}
                />
            </form>
        );
    }
}

export default SearchBar;
