import React, {ReactNode} from "react";
import Request from "../../API/Request";
import PopularLobby from "./PopularLobby";

interface PopularLobbiesState {
    lobbies: any[],
}

class PopularLobbies extends React.Component<any, PopularLobbiesState> {
    constructor(props: any) {
        super(props);
        this.state = {
            lobbies: [],
        }
        this.fillLobbies = this.fillLobbies.bind(this);
        this.refreshLobbies = this.refreshLobbies.bind(this);
        this.renderLobbies = this.renderLobbies.bind(this);
    }

    public componentDidMount(): void {
        this.refreshLobbies();
    }

    public fillLobbies(data: any): void {
        this.setState({lobbies: data});
    }

    public renderLobbies(): ReactNode {
        return this.state.lobbies.map(
            lobby =>
                <PopularLobby
                    key={lobby['id_lobby']}
                    id={lobby['id_lobby']}
                    label={lobby['label_lobby']}
                    description={lobby['description']}
                    logo={lobby['logo']}
                    pseudo={lobby['pseudo']}
                />
        );
    }

    public refreshLobbies(): void {
        new Request(
            '/lobby/getLobbies/0',
            this.fillLobbies,
        );
    }

    public render(): ReactNode {
        return (
            <div className={'container-fluid'}>
                <div className={'row container-fluid pl-0'}>
                    <div className={'row col-12'}>
                        <div className={'col-12 text-left'}>
                            <h1>Lobbies populaires</h1>
                        </div>
                    </div>
                    <div className={'row'}>
                        <div className={'col-12 ml-4'}>
                            <div className={'ml-1'}>
                                <p>Ici tu pourras trouver les lobbies les plus populaires</p>
                            </div>
                        </div>
                    </div>
                </div>
                <div className={'row container-fluid pt-lg-5 pt-md-5 pr-0'}>
                    {this.renderLobbies()}
                </div>
            </div>)
    }
}

export default PopularLobbies;
