import React from 'react';
import logo from './logo.svg';
import userIcon from './icon-female-user.svg'
import './App.css';

class Nav extends React.Component {
  render() {
    return (
      <nav className="row mt-3">
        <span className="glyphicon glyphicon-home col-sm-1" style={{ fontSize: '24px' }} ></span>
        <a href="/" className="mt-1 col-sm-2">caCompilePas</a>
        <form className="form-inline my-2 my-lg-0 ml-5 col-sm-7">
          <label id="search-icon" htmlFor="search">
            <span className="glyphicon glyphicon-search search-icon"></span>
            <span id="search-placeholder">Search...</span>
          </label>
          <input id="search" className="form-control mr-sm-2" type="search" aria-label="Search" />
        </form>
        <span id="user" className="glyphicon glyphicon-user mr-0 d-block col-sm-1 mt-1"></span>
      </nav>
    )
  }
}

class Lobby extends React.Component {
  render() {
    return (
      <section className="row container-fluid pr-0">
        <LobbyTop/>
        <LobbyBody/>
      </section>
    )
  }
}

class LobbyTop extends React.Component {
  render() {
    return (
      <div className="row col-12 mt-5 no-padding-right">
        <LobbySummary/>
        <LobbyDescription/>
      </div>
    )
  }
}

class LobbySummary  extends React.Component {
  render() {
    return (
      <section className="col-5 ml-0 pl-0 top-section">
        <h2 className="text-left mb-0">Sommaire</h2>
        <SummaryList/>
        <div className="dropdown-divider"></div>
      </section>
    )
  }
}

class SummaryList extends React.Component {
  render() {
    return (
      <ul className="ccp-list list-unstyled text-left ml-1 mt-3">
        <li>Les lambdas</li>
        <li>Les HashMaps</li>
        <li>Function&lt;E,T&gt;</li>
        <li>Le SYSOUT</li>
        <li>Les HashMaps</li>
        <li>Function&lt;E,T&gt;</li>
        <li>Le SYSOUT</li>
        <li>Les HashMaps</li>
        <li>Function&lt;E,T&gt;</li>
        <li>Le SYSOUT</li>
      </ul>
    )
  }
}

class LobbyDescription extends React.Component {
  render() {
    return (
      <section className="col-6 no-padding-right top-section">
        <h2 className="ml-lg-0 ml-sm-1 text-left">Cours JAVA avancés</h2>
        <p className="ml-lg-2 ml-sm-3 course-sheet-presentation">Le Lorem Ipsum est simplement du faux texte employé dans la composition et la mise en page avant impression. Le Lorem Ipsum est le faux texte standard de l'imprimerie depuis les années 1500, quand un imprimeur anonyme assembla ensemble des morceaux de texte pour réaliser un livre spécimen de polices de texte.</p>
        <div className="dropdown-divider"></div>
      </section>
    )
  }
}

class Separator extends React.Component {
  render() {
    return (
      <div className="separator">
      </div>
    )
  }
}

class LobbyBody extends React.Component {
  render() {
    return (
      <div>
        <Messages/>
        <CourseSheets/>
      </div>
    )
  }
}

class Messages extends React.Component {
  constructor(props) {
    super(props);
    this.renderMessage = this.renderMessage.bind(this);
    this.renderMessages = this.renderMessages.bind(this);
  }
  renderMessage() {
    return <Message/>;
  }
  renderMessages() {
    let res = [];
    for (let i = 0; i < 10; i++)
      res.push(this.renderMessage());
    return res;
  }
  render() {
    return (
      <ul className="col-sm-6 list-unstyled pl-0">
        { this.renderMessages() }
      </ul>
    )
  }
}

class Message extends React.Component {
  render() {
    return (
      <li className="message row">
        <img src={ userIcon } className="App-logo col-sm-3 pr-sm-0 mt-sm-3" alt="logo" />
        <h4 className="col-sm-9 mt-sm-5 h-25">Bidule</h4>
        <p className="col-sm-9 offset-3">Le Lorem Ipsum est simplement du faux texte employé dans la composition et la mise en page avant impression.</p>
      </li>
    )
  }
}

class CourseSheets extends React.Component {
  render() {
    return (
      <CourseSheet/>
    )
  }
}

class CourseSheet extends React.Component {
  render() {
    return (
      <div className="course-sheet-card">
        <img className="course-sheet-image" />
        <h3>Titre de la fiche</h3>
        <div className="course-sheet-presentation">
          <p className="course-sheet-description">Le Lorem Ipsum est simplement du faux texte employé dans la composition et la mise en page avant impression. Le Lorem Ipsum est le faux texte standard de l'imprimerie depuis les années 1500, quand un imprimeur anonyme assembla ensemble des morceaux de texte pour réaliser un livre spécimen de polices de texte.</p>
          <footer className="course-sheet-presentation-footer">
            <a href="#">Lien vers la fiche</a>
            <p>Auteur</p>
          </footer>
        </div>
      </div>
    )
  }
}

function App() {
  return (
    <div className="App container-fluid">
      <Nav />
      <Lobby />
    </div>
  );
}

