  const canvasRef = React.useRef(null)
  useEffect(() => {
  }, []);

  return (
    <div>
      <canvas 
        ref={ canvasRef }
        width={ window.innerWidth }
        height={ window.innerHeight }
        onClick={e => {
          let x = e.clientX
          let y = e.clientY
          let coor = [ x, y ]
        }}>
          <Figure canvas={ canvasRef.current }/>
        </canvas>
    </div>
  )
}

const Figure = (props) => {
