import React, { ReactDOM, useState, useEffect } from 'react'
import Nav from './Nav'
import Footer from './Footer'
import Lobby from './Lobby'
import './App.css'
import coin from './coin.svg'

const Canvas = () => {
  const canvasRef = React.useRef(null)
  let [canvas, setCanvas] = useState(null)
  const [objects] = useState([])
  const ctx = canvas.getContext('2d')

  const drawCircle = (y, x, radius) => {
    ctx.beginPath()
    ctx.arc(y, x, radius, 0, 2 * Math.PI)
    ctx.stroke()
    ctx.fill()
    ctx.strokeStyle = 'white'
    ctx.fillStyle = 'white'
    ctx.x = 600
    ctx.closePath()
  }
  const renderCirle = (y, x) => {
    drawCircle(y, x, 50)
    let object = {
      height: 40,
      height: 40,
      x: 10,
      y: 10,
      radius: 50,
      color: '#FF0000'
    }
  }
  let x = 400
  let y = 400
  const move = () => {
    renderCirle(x, y, 400)
  }
  let id = setInterval(move, 5)
  useEffect(() => {
    canvas = canvasRef.current
    setCanvas(canvas)
    drawCircle(100, 75, 50)
    drawCircle(200, 300, 50)
    renderCirle(500, 50, 50);
  });
  return (
    <div>
      <canvas
        ref={canvasRef}
        width={window.innerWidth}
        height={window.innerHeight}
        onClick={e => {
          let x = e.clientX
          let y = e.clientY
          let coor = [x, y]
          console.log(coor)
        }}>
      </canvas>
    </div>
  )
}

const Bakground = (props) => {
  const renderFigure = () => <Canvas />
  return (
    <div>
      {renderFigure()}
      {props.children}
    </div>
  )
}

function App() {
  return (
    <div className="App container-fluid ml-lg-4">
      <Bakground>
        <img alt="PiÃ¨ce" src={coin} class="svg-2pomhL coin2-2eQxxG float-21_26k" />
        <Nav />
        <Lobby />
        <Footer /></Bakground>
    </div>
  );
}

export default App;
