import React, {ChangeEvent, ReactNode} from "react";
import Input from '../General/Input';
import minusIcon from '../../img/minus-icon-red-t.png';

interface UserProps {
    id: string,
    pseudo: string,
    writeRight: boolean,
    icon: string,
    toggleWriteRights: (event: React.ChangeEvent<HTMLInputElement>) => void,
    delete: (event: React.MouseEvent<HTMLImageElement, MouseEvent>) => void,
}

class User extends React.Component<UserProps, any> {
    public render(): ReactNode {
        return (
            <div id={'user-' + this.props.id} className={'user-card row mt-5 col-lg-12 col-md-12 col-sm-12 col-xs-12'}>
                <div
                    className={'row col-lg-12 col-md-12 col-sm-12 col-xs-12 mt-lg-4 mt-md-4 mt-sm-4 mt-xs-4 pl-lg-0 pl-md-0 pl-sm-0 pl-xs-0 pr-lg-0 pr-md-0 pr-sm-0 pr-xs-0'}
                >
                    <h3 className={'col-lg-11 col-md-11 col-sm-11 col-xs-11 text-lg-left text-md-left text-sm-left text-xs-left'}>{this.props.pseudo}</h3>
                    <div
                        className={'col-lg-1 col-md-1 col-sm-1 col-xs-1 pr-0 mt-4'}
                    >
                        <img
                            id={'user-remove-' + this.props.id}
                            src={minusIcon}
                            alt={'Minus Icon'}
                            className={'remove-button plus-icon ml-5 mr-0'}
                            onClick={this.props.delete}
                        />
                    </div>
                </div>
                <div
                    className={'row col-lg-12 col-md-12 col-sm-12 col-xs-12 mt-lg-2 mt-md-2 mt-sm-2 mt-xs-2 rounded'}
                >
                    <img
                        src={this.props.icon}
                        className={'user-icon col-2 pl-0'}
                    />
                    <div className={'row col-10 pt-2'}>
                        <div className={'col-2 pt-2'}>
                            <Input
                                id={this.props.id}
                                inputType={'checkbox'}
                                placeholder={''}
                                className={''}
                                onChange={this.props.toggleWriteRights}
                            />
                        </div>
                        <h4 className={'col-10'}>A le droit de modifier le lobby : {this.props.writeRight}</h4>
                    </div>
                </div>
            </div>
        );
    }
}

export default User;
