import React, {ReactNode, Ref} from 'react';

class Hashtag extends React.Component<{ text: string }, { width: number}> {
    private hashtagBox: Ref<HTMLDivElement>;

    constructor(props: any) {
        super(props);
        this.hashtagBox = React.createRef();
    }

    public render(): ReactNode {
        let div = (
            <div key={this.props.text}
                 style={{
                     backgroundColor: 'black',
                     opacity: 0.3,
                     zIndex: 1,
                 }}
                 className={'rounded p-1 pl-2 pr-2 m-1'}
                 ref={this.hashtagBox}
            >
                        <span
                            style={{
                                color: 'white',
                                fontWeight: 'bold',
                            }}
                        >
                            #{this.props.text}
                        </span>
            </div>
        );
        console.log(div);
        // @ts-ignore
        if (null !== this.hashtagBox.current) {
            // @ts-ignore
            console.log(this.hashtagBox.current.width);
        }
        return div;
    }
}

export default Hashtag;
